const SET_EMPLOYEES = 'SET_EMPLOYEES';
const SET_EMPLOYEES_TOTAL_COUNT = 'SET_EMPLOYEES_TOTAL_COUNT';
const REMOVE_ITEM = 'REMOVE_ITEM';
const ADD_ITEM = 'ADD_ITEM';
const SET_PAGE = 'SET_PAGE';


let initialState = {
    coordinates: [
        {
            id: 1,
            latitude: 25.555,
            longitude: 55.2555,
            date: new Date('December 17, 1995 03:24:00')
        }
    ]

};

const MainReducers = (state = initialState, action) => {

    switch (action.type) {
        case SET_PAGE: {
            return {
                ...state,
                page: action.page
            }
        }
        case SET_EMPLOYEES: {
            return {
                ...state,
                coordinates: [...action.coordinates]
            }
        }
        case SET_EMPLOYEES_TOTAL_COUNT: {
            return {
                ...state,
                total: action.total
            }
        }
        case REMOVE_ITEM: {
            return {coordinates: [...state.coordinates].filter(coordinates => coordinates.id !== action.id)}
        }
        case ADD_ITEM: {
            debugger
            const newItem = {
                latitude: action.latitude,
                longitude: action.longitude,
                date: new Date(),
                id: ((new Date()).getTime())
            }
            return {
                ...state,
                coordinates: [...state.coordinates, newItem],
            }
        }

        default:
            return state;
    }

};


