import React from 'react'
import {GoogleMap, LoadScript, Marker} from '@react-google-maps/api';
import Geocode from "react-geocode";






const GeoMap = (props) => {
    debugger

    const geoFindMe = async () => {

        const res = await navigator.geolocation.getCurrentPosition(success);

        const success =  position => {
            const latitude = position.coords.latitude;
            const longitude = position.coords.longitude;


             Geocode.setApiKey("AIzaSyDal_A90HrAXBxIgC4jH4MyCKcbPTSnlJo");

             Geocode.fromLatLng(props.latitude, props.longitude).then(
                response => {
                    debugger
                    const address = response.results[0].formatted_address;
                    console.log(address);
                    props.addItem(latitude, longitude, address)
                },
                error => {
                    console.error(error);
                }
            );



            console.log(latitude, longitude)
        };

    };



    const containerStyle = {
        margin: "auto",
        width: '70vw',
        height: '100vh'
    };

    const center = {
        lat: props.latitude,
        lng: props.longitude
    };

    const onLoad = marker => {
        console.log('marker: ', marker)
    }
    return (

        <div>
            <button onClick={() => geoFindMe()}>Find me</button>

            <div>
                Latitude: {props.latitude}, Longitude: {props.longitude}
                <p>
                    Address: {props.address}
                </p>
            </div>

            <LoadScript
                googleMapsApiKey="AIzaSyDal_A90HrAXBxIgC4jH4MyCKcbPTSnlJo"
            >
                <GoogleMap
                    mapContainerStyle={containerStyle}
                    center={center}
                    zoom={19}
                >
                    <Marker
                        onLoad={onLoad}
                        position={center}
                    />
                    <></>
                </GoogleMap>
            </LoadScript>
        </div>
    )
}

export default GeoMap;
