import React from 'react'
import {GoogleMap, LoadScript, Marker} from '@react-google-maps/api';
import Geocode from "react-geocode";
import ReactWeather from 'react-open-weather';
import 'react-open-weather/lib/css/ReactWeather.css';






const GeoMap = (props) => {

    const geoFindMe = () => {

        const success =  position => {
            const latitude = position.coords.latitude;
            const longitude = position.coords.longitude;

             Geocode.setApiKey("AIzaSyDal_A90HrAXBxIgC4jH4MyCKcbPTSnlJo");

             Geocode.fromLatLng(latitude, longitude).then(
                response => {
                    debugger
                    const address = response.results[0].formatted_address;
                    console.log(address);
                    props.addItem(latitude, longitude, address)

                },
                error => {
                    console.error(error);
                }
            );



            console.log(latitude, longitude)
        };
        navigator.geolocation.getCurrentPosition(success);
    };



    const containerStyle = {
        margin: "auto",
        width: '70vw',
        height: '100vh'
    };

    const center = {
        lat: props.latitude,
        lng: props.longitude
    };

    const onLoad = marker => {
        console.log('marker: ', marker)
    }
    return (

        <div>
            <button onClick={() => geoFindMe()}>Find me</button>

            <div>
                Latitude: {props.latitude}, Longitude: {props.longitude}
                <p>
                    Address: {props.address}
                </p>
            </div>


            <ReactWeather
                forecast="today"
                apikey="7d3e7611117646442721f66c163412ea"
                type="geo"
                lat="48.1351"
                lon="11.5820"
            />

            <ReactWeather
                forecast="today"
                apikey="7d3e7611117646442721f66c163412ea"
                type="city"
                city="Munich"
            />


            <LoadScript
                googleMapsApiKey="AIzaSyDal_A90HrAXBxIgC4jH4MyCKcbPTSnlJo"
            >
                <GoogleMap
                    mapContainerStyle={containerStyle}
                    center={center}
                    zoom={19}
                >
                    <Marker
                        onLoad={onLoad}
                        position={center}
                    />
                    <></>
                </GoogleMap>
            </LoadScript>
        </div>
    )
}

export default GeoMap;
